mapper.select:
SELECT co.noticeid
FROM mishu_write.collec_notice co
WHERE co.noticeid IN

   #{noticeId}

AND  userid=#{userId}

SELECT  cc.companyId
FROM mishu_write.colle_company_new cc
WHERE cc.companyId IN

#{id}

AND  userid=#{userId}

join mishu.snatch_url_cert suc on suc.contId=s.id
and
suc.certificateuuid like '%${comThreeApt}%'
and
suc.certificateuuid like '%${zzTypeThree}%'
and suc.certificateuuid not like '%${notThreeApt}%'
and suc.certificateuuid like '%${zzTypeTwo}%'
join (select distinct concat('%',majoruuid,'%') as majorUuid from mishu.aptitude_dictionary where aptitudeCode=#{zzTypeOne}) zzdic on suc.certificateUuid like zzdic.majorUuid
join mishu.${detailTable} detail on s.id=detail.snatchurlid
AND LENGTH(detail.bmSite)>1 AND detail.bmSite not like '%网上%'
AND detail.bmSite like #{bmSiteStr}
AND detail.pbMode in(${modeStr})
AND detail.projSum >= #{projSumStart}
and detail.projSum <= #{projSumEnd}
AND detail.tbEndDate >= #{kbDateStart}
AND detail.tbEndDate <= #{kbDateEnd}
AND detail.oneName = #{oneName}
WHERE s.isShow = 0
AND s.biddingType =#{projectType}
and s.province =(select name_abbr from mishu.area ar where ar.name LIKE '${province}%' and grade=0)
AND s.city in(select name_abbr from mishu.area where display_name like '%${city}%')
AND ( detail.projXs like CONCAT(#{county},'%') or s.county = (select name_abbr from mishu.area where display_name like CONCAT(#{county},'%')) )
AND s.title LIKE '%${title}%'
AND !(detail.projSum REGEXP '[^0-9.]') AND detail.projSum !=''
and (s.type =0 or s.type=2)
and s.type = #{type}

SELECT group_concat(DISTINCT zz.certificateuuid separator ',') as zzlist
FROM mishu.snatch_url_cert zz
where zz.contid=#{id}

select com_id,`range`
from mishu.tb_company
where com_name = #{com_name}
