mapper.select:
select zh.mateName,zh.score from cert_aqrz a 
left join prize_zh zh on a.zhUuid=zh.uuid 
where a.zhUuid is not null and a.yxqTime > date(now()) and srcUuid in

  #{item}

order by zh.score desc limit 1;

select q.projName,q.projType,q.joinType,q.years,zh.type,zh.code,zh.mateName,zh.score,zh.shortName,zh.shortRemark from cert_qyhj q left join prize_zh zh on q.zhUuid=zh.uuid where q.zhUuid is not null and q.joinType='承建单位' and srcUuid in
#{item}
order by zh.score desc

select c.* from cert_aqrz a left join cert_src s on a.srcUuid=s.`uuid` left join tb_company c on c.com_name = s.companyName where c.com_id is not null
and c.com_name like CONCAT('%',#{keyWord},'%')
and c.regis_address like CONCAT('%',#{province},'%')
and
c.regis_address like CONCAT(#{list},'%')
and c.regis_capital >= #{minCapital}
and c.regis_capital <= #{maxCapital}
and c.range like CONCAT('%',#{qualCode},'%')
and
c.range like CONCAT('%',#{zzList},'%')
and a.secureLevel=#{level}
and a.secureRank=#{rank}
order by c.px desc

select com_name,com_code
from mishu.tb_company
where com_name like #{name} or com_name_py like #{name}
order by px desc
limit #{count}
