mapper.select:
select
t.com_name as comName,


        SUM(t.num) as num


        t.num as num


from tb_count_bid_info t
where


        t.stat_date >= #{statDate}
        and t.stat_date <= #{endDate}


        t.stat_date = #{statDate}




        GROUP BY t.`com_name`
        HAVING SUM(t.num) >= #{count}
        ORDER BY SUM(t.num) DESC


        and t.num >= #{count}
        order by t.num desc

choose.otherwise:
select ifnull(count(t.com_name),0) as comNameCount,
ifnull(sum(t.num),0) as sumNum
from tb_count_bid_info t
where
t.stat_date = #{statDate}
and t.num >= #{count}

mapper.select:
select count(1)
from tb_count_bid_info t
where


        t.stat_date >= #{statDate}
        and t.stat_date <= #{endDate}


        t.stat_date = #{statDate}
